import { NextResponse } from 'next/server';
import { analyzeProductImage } from '../../../utils/gemini';

// This is a test image base64 string - a small 1x1 pixel transparent PNG
const TEST_IMAGE = "data:image/jpeg;base64,";

export async function GET() {
  try {
    console.log('Testing Gemini API with sample image...');
    
    const analysis = await analyzeProductImage(TEST_IMAGE);
    
    console.log('Analysis result:', analysis);

    return NextResponse.json({
      success: true,
      analysis
    });
  } catch (error) {
    console.error('Error in test-analysis route:', error);
    return NextResponse.json(
      { 
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error'
      },
      { status: 500 }
    );
  }
} 